import { Component, OnInit } from '@angular/core';
import { AccountService } from '../_services/account.service';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent implements OnInit{
  model: any={};
  loggedIn=false;
  constructor(private accountService: AccountService) {

  }
  ngOnInit(): void {
  }

  login(){
    this.accountService.login(this.model).subscribe({
      next: response=>{
        console.log(response);
        this.loggedIn=true;
      },
      error: error=> console.log(error)
    });
  }
  logout(){
    this.loggedIn=false;
  }

}
<nav class="navbar navbar-expand-md navbar-dark fixed-top bg-dark">
    <div class="container">
      <a class="navbar-brand" href="#">Dating APP</a>
      <ul class="navbar-nav me-auto mb-2 mb-md-0" *ngIf="loggedIn">
        <li class="nav-item">
          <a class="nav-link" href="#">Matches</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">List</a>
        </li>
        <li class="nav-item">
          <a class="nav-link">Messages</a>
        </li>
      </ul>
      <div class="dropdown">
        <a class="dropdown-toggle text-light">Welcome user</a>
        <div class="dropdown-menu">
          <a  class="dropdown-item">Edit Profile</a>
          <a  class="dropdown-item" (click)="logout()">Logout</a>
        </div>
      </div>
      <form #loginForm="ngForm" class="d-flex" (ngSubmit)="login()" autocomplete="off">
        <input name="username" [(ngModel)]="model.username" class="form-control me-2" type="text" placeholder="Username">
        <input name="password" [(ngModel)]="model.password" class="form-control me-2" type="password" placeholder="Password">
        <button class="btn btn-outline-success" type="submit">Login</button>
      </form>
    </div>
  </nav>
